import logo from './logo.svg';
import './App.css';
import axios from 'axios';
import HomePage from './components/HomePage';
import Tasks from './components/Tasks';
import { useEffect, useState } from 'react';
import ShopCart from './components/ShopCart';

function App() {

  const [products, setProducts] = useState([]);
  const [cartList, setCartList] = useState([])


  const getProducts = () => {
    axios.get('https://fakestoreapi.com/products')
      .then((res) => {
        setProducts(res.data);
        console.log(res.data);

      })
      .catch((error) => {
        console.error(error);
      })

  }
  const addToCart = (product) => {
    console.log(product);
    setCartList([...cartList, product])

  }

  const removeCart = (id) => {
    const updatedProduct= props.products.filter(product => product.id !== id);
    props.setCartList(updatedProduct);
  }

  useEffect(() => {
    getProducts()
  }, []);



  return (
    <div className="App">
      <HomePage products={products} addToCart={addToCart} removeCart={removeCart}/>
      <Tasks products={products} cartList={cartList}/>

    </div>
  );


}

export default App;
